package com.uc.blogs.api.enums;public enum ErrorCode {    SYSTEM_EXCEPTION(1000,"系统异常"),    PARAM_ILLEGAL(1001,"请求参数有误"),    SIGNATURE_ILLEGAL(1002,"签名有误");    private Integer code;    private String message;    ErrorCode(Integer code, String message) {        this.code = code;        this.message = message;    }    public Integer getCode() {        return code;    }    public String getMessage() {        return message;    }}