package com.uc.blogs.api.exception;import com.uc.blogs.api.enums.ErrorCode;/** * @author 9527 * @ClassName BlogsMoudleException * @Date 2020/3/10 * @Description TODO * @Version 1.0 */public class BlogsMoudleException extends Exception {    private static final long serialVersionUID = 5343755180485796195L;    private ErrorCode errorCode;    private Throwable throwable;    public BlogsMoudleException(ErrorCode errorCode, String message, Throwable cause) {        super(message, cause);        this.errorCode = errorCode;        this.throwable = cause;    }    public BlogsMoudleException(ErrorCode errorCode, String message) {        super(message);        this.errorCode = errorCode;    }    public int getErrCode() {        return errorCode.getCode();    }    @Override    public String getMessage() {        if (throwable == null) {            return errorCode.getMessage();        }        return throwable.getMessage();    }}