package com.uc.blogs.api.exception;import com.uc.blogs.api.enums.ErrorCode;/** * @author 9527 * @ClassName ParamIllegalException * @Date 2020/3/10 * @Description TODO * @Version 1.0 */public class ParamIllegalException extends Exception{    private String message;    private ErrorCode errorCode;    private Throwable throwable;    public ParamIllegalException(String message, ErrorCode errorCode,Throwable throwable) {        super(message,throwable);        this.errorCode = errorCode;        this.throwable = throwable;    }    public ParamIllegalException(String message, ErrorCode errorCode) {        super(message);        this.errorCode = errorCode;    }    public int getErrCode() {        return errorCode.getCode();    }    @Override    public String getMessage() {        if (throwable == null) {            return errorCode.getMessage();        }        return throwable.getMessage();    }}