package com.uc.blogs.api.exception;import com.uc.blogs.api.enums.ErrorCode;/** * @author 9527 * @ClassName SignatureFailException * @Date 2020/3/10 * @Description TODO * @Version 1.0 */public class SignatureFailException extends Exception{    private ErrorCode errorCode;    private Throwable throwable;    public SignatureFailException(ErrorCode errorCode, String message, Throwable cause) {        super(message, cause);        this.errorCode = errorCode;        this.throwable = cause;    }    public SignatureFailException(ErrorCode errorCode, String message) {        super(message);        this.errorCode = errorCode;    }    public int getErrCode() {        return errorCode.getCode();    }    @Override    public String getMessage() {        if (throwable == null) {            return errorCode.getMessage();        }        return throwable.getMessage();    }}