package com.uc.moudle.blogs.provider.controller;import com.uc.blogs.api.enums.ErrorCode;import com.uc.blogs.api.exception.BlogsMoudleException;import com.uc.blogs.api.service.FileManagerService;import com.ucs.moudlecommon.response.BaseResponse;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.MediaType;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.*;import org.springframework.web.multipart.MultipartFile;/** * @author 9527 * @ClassName FileManagerController * @Date 2020/4/4 * @Description TODO * @Version 1.0 */@Controller@RequestMapping(value = "/file")public class FileManagerController {    private Logger logger = LoggerFactory.getLogger(FileManagerController.class);    @Autowired    private FileManagerService fileManagerService;    @RequestMapping(value = "/multiFileUpload" ,method = RequestMethod.POST, consumes = MediaType.MULTIPART_FORM_DATA_VALUE)    @ResponseBody    public BaseResponse multiFileUpload(@RequestPart(value = "multipartFiles") MultipartFile[] multipartFiles, @RequestParam(value = "uploadType") String uploadType){        logger.info("上传文件服务");        BaseResponse response = new BaseResponse();        try {            response.setResult(fileManagerService.multiFileUpload(multipartFiles,uploadType));        }catch (BlogsMoudleException e){            response.setCode(e.getErrCode());            response.setMessage(e.getMessage());        }catch (Exception e){            response.setCode(ErrorCode.SYSTEM_EXCEPTION.getCode());            response.setMessage(ErrorCode.SYSTEM_EXCEPTION.getMessage());        }        return response;    }}