package com.uc.moudle.blogs.provider.serviceImpl;import com.uc.blogs.api.entity.UcBlogs;import com.uc.blogs.api.enums.ErrorCode;import com.uc.blogs.api.exception.BlogsMoudleException;import com.uc.blogs.api.moudle.BlogIndexMoudle;import com.uc.blogs.api.service.BlogsService;import com.uc.moudle.blogs.provider.mapper.BlogsMapper;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import java.util.List;/** * @author 9527 * @ClassName BlogServiceImpl * @Date 2020/3/11 * @Description TODO * @Version 1.0 */@Service@Transactional(readOnly = true,rollbackFor = {Exception.class})public class BlogServiceImpl implements BlogsService {    @Autowired    private BlogsMapper blogsMapper;    @Override    public List<UcBlogs> findBlogsByUserId(Integer status, Integer userId) throws Exception{        if(status == null || userId == null){            throw new BlogsMoudleException(ErrorCode.PARAM_ILLEGAL,"请求参数有误");        }        List<UcBlogs> blogs = blogsMapper.queryAllByUserId(status,userId);        return blogs;    }    @Override    public UcBlogs blogDetail(Integer blogId) throws Exception{        if(blogId == null){            throw new BlogsMoudleException(ErrorCode.PARAM_ILLEGAL,"请求参数有误");        }        UcBlogs blog = blogsMapper.blogDetail(blogId,0);        return blog;    }}